INSERT INTO `m_letter` VALUES (13, 1, 1, '12/2567', 12, 2024, 'บริษัท', '2024-09-28', 'ประวัติความเป็นมานิทานอีสป', 'นายAdmin SuperAdmin (ผู้บริหาร) (Hr),นายasdf asdf (ผู้บริหาร) (Hr),นายasdf asdf (ผู้บริหาร) (Hr)', '<p>	เรื่องราวและที่มาของนิทานอีสป เนื่องจากอีสปมีชีวิตอยู่เมื่อหลายหลายพันปีล่วงมาแล้ว จึงยากที่จะได้ข้อมูลเกี่ยวข้องกับประวัติของอีสปที่ถูกต้องสมบูรณ์ครบถ้วน อย่างไรก็ตาม แม้จะมีข้อมูลอยู่หลายกระแส แต่นักค้นคว้าประวัติส่วนใหญ่ของอีสปก็เห็นพ้องต้องกันว่าข้อมูลต่อไปนี้ \"น่าจะเป็นเรื่องราวของอีสปที่ถูกต้องมากที่สุด\"&nbsp;</p><p>&nbsp;&nbsp;&nbsp;อีสป เป็นทาสชาวกรีกคนหนึ่งซึ่งมีชีวิตอยู่ในช่วงเวลา 560 - 620 ปีก่อนคริสต์ศักราช หรือ 208 ปี ก่อนพุทธศักราช (พระพุทธเจ้าประสูตรเมื่อ 80 ปี ก่อนพุทธศักราช) นับเวลาถึงปัจจุบันได้ 2,755 - 2,815 ปี เขาอาศัยอยู่ที่เมืองซาร์ดิสบนเกาะซามอสของประเทศกรีก เกาะนี้ตั้งอยู่ที่นอกชายฝั่งของประเทศตุรกีในปัจจุบัน ในสมัยกรีกโบราณชายฝั่งทะเลทั้งหมดของประเทศตุรกีก็มีชาวกรีกอาศัยอยู่อย่างหนาแน่น อีสปเป็นคนพิการ ขี่เหร่ แต่เขามีจิตใจที่งดงาม ซึ่งตรงกันข้ามกับสังขารของเขา เริ่มแรกนั้นอีสปมาจากเทรซซึ่งเป็นนครรัฐแห่งหนึ่งในสมัยโบราณ ปัจจุบันเทรซเป็นดินแดนส่วนหนึ่งของกรีกและบัลแกเรีย อีสปไปทำงานเป็นทาสที่เกาะซามอสกับนายทาส ชื่อเอียดมอน ในระหว่างที่เป็นทาส อีสปได้นำชื่อเสียงมาสู่ตนเองและนายของเขาด้วยการเป็นนักเล่านิทานผู้มีความสามารถจนเป็นที่รู้จักกันดีในท้องถิ่นนั้น ในที่สุดอีสปก็ถูกปลดปล่อยให้เป็นอิสระจากการเป็นทาส เนื่องจากความเป็นผู้ที่มีไหวพริบและสติปัญญาอันเฉียบแหลมของเขานั่นเอง&nbsp;</p><p><br></p>', 1, NULL, 7, 'testa', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
